import React from 'react'
// We'll need React Router's own version of the History API
import { useHistory } from 'react-router-dom'

export default function Home() {
    // ðŸ‘‰ STEP 5 - Build a click handler that will imperatively
    // navigate us to <website base URL>/items-list

    const history = useHistory()

    const routeToShop = () => {
        //react router version of the history API
        //for example, we could navigate to different places,
        //after checking auth status (make sure user has permission to be in that part of our app)
        history.push('/items-list');
    }

    return (
        <div className='home-wrapper'>
            <img
                className='home-image'
                src='https://source.unsplash.com/F6-U5fGAOik'
                alt=''
            />
            <button
                onClick={routeToShop}
                className='md-button shop-button'
            >
                Shop now!
      </button>
        </div>
    )
}
